    // get data from blogger
    function getPostsPerPage() {

        $('#blog-posts-wrapper #posts-wrap').empty();
        $('#blog-posts-pagination ul.pagination').empty();
        $('#blog-posts-wrapper .loader-wrap').show();

        $.ajax({
            url: "https://sureshbabudj.blogspot.com/feeds/posts/default?alt=json&max-results=" + pageLimit + "&start-index=" + startIndex,
            jsonp: "callback",
            dataType: "jsonp",
            data: {
                format: "json"
            },
            success: function (response) {
                $('#blog-posts-wrapper .loader-wrap').hide();
                createPortfolio(response);
            },
            error: function (err) {
                $('#blog-posts-wrapper .loader-wrap').hide();
                $('#blog-posts-wrapper').append('<div class="error">Something went wrong while fetching portfolio items</div>');
                console.log(err);
            }
        });
    }

    // dynamic creation of portfolio items based on the data

    function createPortfolio(response) {
        var randomImage = true; // to display randome image
        var posts = response.feed.entry;
        var pf;
        if (!posts || !posts.length) {
            pf = $('<p>WIP: I am working on new portfolio design. Sorry for inconvenience... </p>');
        } else {
            pf = $('<div class="blog-posts"></div>');
            for (i = 0; i < posts.length; i++) {
                var randomId = Math.random().toString(36).substring(7);
                var post = $('<div id="brick-' + randomId + '" href="' + posts[i].link[4].href + '" class="brick post-item"></div>');
                var postInner = $('<div id="inner-' + randomId + '" class="inner"></div>');
                $(post).append(postInner);
                var img = $('<div id="img-wrap-' + randomId + '" class="img-wrap"></div>');
                if (posts[i] && posts[i].media$thumbnail && posts[i].media$thumbnail.url) {
                    var postImg = posts[i].media$thumbnail.url.replace('s72-c', 's480');
                    var image = $('<img id="img-' + randomId + '" src="' + postImg + '" />');
                    $(img).append(image);
                } else if (randomImage) {
                    var postImg = 'https://loremflickr.com/480/240';
                    if (posts[i].category && posts[i].category.length) {
                        var index = Math.floor(Math.random() * (posts[i].category.length - 1)) + 1;
                        var postImg = `https://loremflickr.com/480/240/${posts[i].category[index].term}/all`;
                    }
                    var image = $('<img id="img-' + randomId + '" src="' + postImg + '" />');
                    $(img).append(image);
                } else {
                    var fallback = $('<div id="img-"' + randomId + ' class="img-fallback"></div>');
                    var imgIcon = $('<i id="icon-' + randomId + '" class="fa fa-image"></i>');
                    $(fallback).append(imgIcon);
                    $(img).append(fallback);
                }
                if (posts[i].category && posts[i].category.length) {
                    var index = Math.floor(Math.random() * (posts[i].category.length - 1)) + 1;
                    var badge = $('<span id="badge-' + randomId + '" class="post-tag badge badge-primary">' + posts[i].category[index].term + '</span>');
                    $(img).append(badge);
                }
                var content = $('<div id="post-item--' + randomId + '" class="post-item-content"></div>');
                var title = $('<h3 id="heading-' + randomId + '" class="post-item-title">' + posts[i].title.$t + '</h3>');
                var para = $('<p id="para-' + randomId + '" class="post-item-para"></p>');
                var temp = document.createElement('div');
                temp.innerHTML = posts[i].content.$t.substring(0, posts[i].content.$t.indexOf('<a name=\'more\'>'));
                para.text($(temp).text());
                var postItemFooter = $('<div id="ftr-' + randomId + '" class="post-item-footer"></div>')
                var published = $(`<span class="post-published">Published on ${new Date(posts[i].published.$t).toDateString()}</span>`);
                var dateIcon = $('<i class="fa fa-calendar"></i>');
                $(published).append(dateIcon);
                var readMoreLink = $('<a id="anchor-' + randomId + '" href="' + posts[i].link[4].href + '" class="read-more-link btn">Read More</a>');
                $(postItemFooter).append(readMoreLink, [published]);
                $(content).append(title, [para, postItemFooter]);
                $(postInner).append(img, [content]);
                $(pf).append(post);
            }
            doPagination(response);
        }
        $('#blog-posts-wrapper #posts-wrap').append(pf);
    }

    function doPagination(response) {
        totalItems = parseInt(response.feed.openSearch$totalResults.$t);
        startIndex = parseInt(response.feed.openSearch$startIndex.$t);
        pageLimit = parseInt(response.feed.openSearch$itemsPerPage.$t);
        totalPages = Math.ceil(totalItems / pageLimit);

        var elm = $('#blog-posts-pagination ul.pagination');
        elm.empty();
        var prevElm = $('<li class="page-item"><a class="page-link"><span aria-hidden="true">«</span></a></li>');
        elm.append(prevElm);
        for (i = 1; i <= totalPages; i++) {
            var currentElm = $('<li class="page-item"><a class="page-link">' + i + '</a></li>');
            if (i === currentPage) {
                currentElm.addClass('active');
            }
            elm.append(currentElm);
        }
        var nextElm = $('<li class="page-item"><a class="page-link"><span aria-hidden="true">»</span></a></li>');
        elm.append(nextElm);

        if (!navRendered) {
            // bind event
            $('body').on('click', 'li.page-item a.page-link', function (e) {
                pageItem = e.target.innerText;
                if (pageItem === '»') {
                    goNext();
                } else if (pageItem === '«') {
                    goBack();
                } else {
                    var pageNo = parseInt(pageItem);
                    goToPageNumber(pageNo);
                }
            });
            navRendered = true;
        }

    }

    function goNext() {
        if ((currentPage + 1) > totalPages) {
            return;
        }
        startIndex = (currentPage * pageLimit) + 1;
        currentPage++;
        getPostsPerPage();
    }

    function goBack() {
        if ((currentPage - 1) <= 0) {
            return;
        }
        currentPage--;
        startIndex = ((currentPage - 1) * pageLimit) + 1;
        getPostsPerPage();
    }

    function goToPageNumber(pageNo) {
        if (pageNo > totalPages || pageNo < 0) {
            return;
        }
        startIndex = ((pageNo - 1) * pageLimit) + 1;
        currentPage = pageNo;
        getPostsPerPage();
    }

    /** portfolio end */

    getPhotos();
    getPostsPerPage();

    /** photo roll end */
})();
